import numpy as np
from scipy.optimize import minimize

def construct_packing():
    n = 26

    def objective(x):
        # Maximize sum of radii
        return -np.sum(x[2*n:])

    def constraints_func(x):
        centers = x[:2*n].reshape(n, 2)
        radii = x[2*n:]

        # Boundary constraints: center_i - radius_i >= 0 and center_i + radius_i <= 1
        boundary = np.concatenate([
            centers[:, 0] - radii,
            1.0 - centers[:, 0] - radii,
            centers[:, 1] - radii,
            1.0 - centers[:, 1] - radii
        ])

        # Non-overlap constraints: dist(c_i, c_j) >= r_i + r_j
        # Using squared distances is more stable, but linear for SLSQP is often fine
        overlaps = []
        for i in range(n):
            for j in range(i + 1, n):
                dx = centers[i, 0] - centers[j, 0]
                dy = centers[i, 1] - centers[j, 1]
                dist = np.sqrt(dx*dx + dy*dy)
                overlaps.append(dist - (radii[i] + radii[j]))

        return np.concatenate([boundary, overlaps])

    # Initial guess: A slightly perturbed hexagonal-like grid
    # For 26 circles, a 5x5 grid + 1 or a staggered 5-5-5-5-6 layout
    def get_initial_guess():
        centers = []
        rows = [5, 5, 6, 5, 5]
        for r, num_cols in enumerate(rows):
            for c in range(num_cols):
                x = (c + 0.5 + (0.1 if r % 2 else 0)) / (num_cols + 0.2)
                y = (r + 0.5) / (len(rows))
                centers.append([x, y])
        centers = np.array(centers[:n])
        radii = np.full(n, 1.0 / (2 * np.sqrt(n)))
        return np.concatenate([centers.flatten(), radii])

    bounds = [(0, 1)] * (2 * n) + [(0, 0.5)] * n

    # Primary Optimization
    x0 = get_initial_guess()
    res = minimize(
        objective, x0, method='SLSQP', 
        constraints={'type': 'ineq', 'fun': constraints_func},
        bounds=bounds, options={'maxiter': 200, 'ftol': 1e-9}
    )

    best_x = res.x
    best_sum = -res.fun

    # Local Search / Refinement
    # We apply small perturbations to escape local plateaus and refine the sum
    for scale in [0.005, 0.001]:
        for _ in range(5):
            x_perturbed = best_x.copy()
            x_perturbed[:2*n] += np.random.normal(0, scale, 2*n)
            x_perturbed = np.clip(x_perturbed, 0, 1)

            res_refine = minimize(
                objective, x_perturbed, method='SLSQP',
                constraints={'type': 'ineq', 'fun': constraints_func},
                bounds=bounds, options={'maxiter': 100, 'ftol': 1e-10}
            )

            if res_refine.success and -res_refine.fun > best_sum:
                # Verify constraints strictly
                if np.all(constraints_func(res_refine.x) >= -1e-10):
                    best_sum = -res_refine.fun
                    best_x = res_refine.x

    # Final polish
    res_final = minimize(
        objective, best_x, method='SLSQP',
        constraints={'type': 'ineq', 'fun': constraints_func},
        bounds=bounds, options={'maxiter': 400, 'ftol': 1e-12}
    )

    if np.all(constraints_func(res_final.x) >= -1e-12):
        best_x = res_final.x
        best_sum = -res_final.fun

    centers = best_x[:2*n].reshape(n, 2)
    radii = best_x[2*n:]

    return centers, radii, best_sum

def run_packing():
    return construct_packing()

