            rows = 5
            cols = 6
            for row in range(rows):
                for col in range(cols):
                    if len(centers) >= n:
                        break
                    x = offset_x + col * 0.15 + (row % 2) * 0.075
                    y = offset_y + row * 0.13
                    if x <= 1 and y <= 1:
                        centers.append([x, y])
                if len(centers) >= n:
                    break

            if len(centers) == n:
                centers = np.array(centers)
                radii = np.full(n, 0.05)
