                for offset_y in np.linspace(0, 0.3, 5):
                    centers = []
                    rows = 5
                    cols = 6
                    for row in range(rows):
                        for col in range(cols):
                            if len(centers) >= n:
                                break
                            x = offset_x + col * spacing_x + (row % 2) * spacing_x * 0.5
                            y = offset_y + row * spacing_y
                            if x <= 1 and y <= 1:
