import numpy as np
from scipy.optimize import minimize

def construct_packing():
    n = 26

    def objective(x):
        # x contains [x1, y1, x2, y2, ..., r1, r2, ...]
        radii = x[2*n:]
        return -np.sum(radii)

    def constraints_func(x):
        centers = x[:2*n].reshape(n, 2)
        radii = x[2*n:]

        # Square boundaries: r_i <= x_i <= 1-r_i and r_i <= y_i <= 1-r_i
        # Equivalent to: x_i - r_i >= 0, 1 - x_i - r_i >= 0, etc.
        c_boundary = []
        for i in range(n):
            c_boundary.extend([
                centers[i, 0] - radii[i],
                1.0 - centers[i, 0] - radii[i],
                centers[i, 1] - radii[i],
                1.0 - centers[i, 1] - radii[i]
            ])

        # No overlap: dist(i, j) >= r_i + r_j
        c_overlap = []
        for i in range(n):
            for j in range(i + 1, n):
                dx = centers[i, 0] - centers[j, 0]
                dy = centers[i, 1] - centers[j, 1]
                dist = np.sqrt(dx*dx + dy*dy)
                c_overlap.append(dist - (radii[i] + radii[j]))

        return np.concatenate([c_boundary, c_overlap])

    def get_greedy_init():
        centers = []
        radii = []
        # Strategy: Place circles in a grid-like fashion but allow them to be large
        # Start with corners
        pts = [[0.1, 0.1], [0.9, 0.1], [0.1, 0.9], [0.9, 0.9]]
        # Fill a 5x5-ish grid
        for y in np.linspace(0.1, 0.9, 5):
            for x in np.linspace(0.1, 0.9, 6):
                if len(pts) < n:
                    pts.append([x, y])

        centers = np.array(pts[:n])
        radii = np.full(n, 0.08) # Initial guess for radii
        return np.concatenate([centers.flatten(), radii])

    def get_hex_init():
        centers = []
        rows, cols = 5, 6
        for r in range(rows):
            for c in range(cols):
                if len(centers) < n:
                    x = 0.1 + c * 0.16 + (r % 2) * 0.08
                    y = 0.1 + r * 0.15
                    if x < 1 and y < 1:
                        centers.append([x, y])
        while len(centers) < n:
            centers.append(np.random.rand(2))
        return np.concatenate([np.array(centers).flatten(), np.full(n, 0.07)])

    best_sum = 0
    best_res = None

    # Try different seeds/initializations
    seeds = [get_greedy_init(), get_hex_init()]
    # Add a few randomized variations of the hex grid
    for _ in range(2):
        seeds.append(get_hex_init() + np.random.normal(0, 0.02, 3*n))

    bounds = [(0, 1)] * (2*n) + [(0, 0.5)] * n

    for init_x in seeds:
        res = minimize(
            objective,
            init_x,
            method='SLSQP',
            bounds=bounds,
            constraints={'type': 'ineq', 'fun': constraints_func},
            options={'maxiter': 200, 'ftol': 1e-7}
        )

        if res.success or True:
            curr_sum = -res.fun
            if curr_sum > best_sum:
                # Double check constraints
                if np.all(constraints_func(res.x) >= -1e-5):
                    best_sum = curr_sum
                    best_res = res.x

    # Final polish on the best result
    res = minimize(
        objective,
        best_res,
        method='SLSQP',
        bounds=bounds,
        constraints={'type': 'ineq', 'fun': constraints_func},
        options={'maxiter': 400, 'ftol': 1e-9}
    )

    final_centers = res.x[:2*n].reshape(n, 2)
    final_radii = res.x[2*n:]
    # Ensure strict feasibility
    final_radii -= 1e-8 

    return final_centers, final_radii, np.sum(final_radii)

def run_packing():
    return construct_packing()

