            result = minimize(
                lambda x: objective(x, n),
                best_result,
                method='SLSQP',
                constraints=constraints,
                options={'maxiter': 3000, 'ftol': 1e-12}
            )
            if result.success and -result.fun > best_sum:
                best_result = result.x
                best_sum = -result.fun
        except:
            pass

        # Local perturbations (20 attempts)
        for _ in range(20):
            perturbed = best_result.copy()
            perturbed[:2*n] += np.random.normal(0, 0.003, 2*n)
            perturbed[:2*n] = np.clip(perturbed[:2*n], 0.01, 0.99)

            try:
                result = minimize(
                    lambda x: objective(x, n),
                    perturbed,
                    method='SLSQP',
                    constraints=constraints,
                    options={'maxiter': 2000, 'ftol': 1e-10}
                )
                if result.success and -result.fun > best_sum:
                    best_result = result.x
                    best_sum = -result.fun
            except:
                continue

    # Extract final solution
    if best_result is not None:
        centers = best_result[:2*n].reshape((n, 2))
        radii = best_result[2*n:3*n]
        sum_radii = np.sum(radii)
    else:
        centers = np.random.uniform(0.3, 0.7, (n, 2))
        radii = np.full(n, 0.01)
        sum_radii = np.sum(radii)

    return centers, radii, sum_radii

